import React from 'react'


const ButterflyAnimation = () => {
  const [position, setPosition] = useState({ x: 50, y: 300 });
  const [time, setTime] = useState(0);
  const [isAnimating, setIsAnimating] = useState(true);
  const [activePoint, setActivePoint] = useState(null);
  const animationRef = useRef(null);

  const points = [
    { x: 100, y: 300, text: "Starting point - The journey begins" },
    { x: 300, y: 300, text: "First milestone reached" },
    { x: 500, y: 300, text: "Halfway there" },
    { x: 700, y: 300, text: "Almost at the destination" },
    { x: 900, y: 300, text: "Journey completed" }
  ];

  useEffect(() => {
    if (!isAnimating) return;

    const animate = () => {
      setTime(prev => {
        if (prev >= 1) {
          setIsAnimating(false);
          return 1;
        }
        return prev + 0.002;
      });
    };

    animationRef.current = requestAnimationFrame(animate);
    return () => cancelAnimationFrame(animationRef.current);
  }, [time, isAnimating]);

  useEffect(() => {
    const x = time * 800 + 100;
    const y = 300 + Math.sin(time * Math.PI * 2) * 50;
    setPosition({ x, y });

    // Check proximity to points
    points.forEach((point, index) => {
      const distance = Math.sqrt(
        Math.pow(point.x - x, 2) + Math.pow(point.y - y, 2)
      );
      if (distance < 30) {
        setActivePoint(index);
      }
    });
  }, [time]);

  return (
    <div className="w-screen h-screen bg-gray-900 relative overflow-hidden">
      {/* Radial gradient background */}
      <div className="absolute inset-0 bg-gradient-to-r from-purple-900 via-gray-900 to-purple-900" />
      
      <svg className="w-full h-full">
        {/* Path line */}
        <path
          d="M100,300 L900,300"
          fill="none"
          stroke="#666"
          strokeWidth="2"
          strokeDasharray="5,5"
        />

        {/* Points */}
        {points.map((point, index) => (
          <g key={index}>
            <circle
              cx={point.x}
              cy={point.y}
              r="8"
              fill={activePoint === index ? "#ffd700" : "#fff"}
              stroke="#666"
              strokeWidth="2"
            />
          </g>
        ))}

        {/* Butterfly */}
        <g transform={`translate(${position.x}, ${position.y})`}>
          <image
            href="/api/placeholder/60/40"
            width="60"
            height="40"
            transform="translate(-30, -20)"
          >
            <animate
              attributeName="y"
              values="-2;2;-2"
              dur="0.5s"
              repeatCount="indefinite"
            />
          </image>
        </g>
      </svg>

      {/* Point text display */}
      {activePoint !== null && (
        <div className="absolute left-1/2 top-1/2 transform -translate-x-1/2 -translate-y-1/2">
          <div className="bg-white/10 backdrop-blur-md p-4 rounded-lg text-white shadow-lg">
            {points[activePoint].text}
          </div>
        </div>
      )}
    </div>
  );
};

export default ButterflyAnimation;
